#include <iostream>
#include <vector>

using namespace std;

class Solution {
public:
	int repeatedStringMatch(string A, string B) {
		string str = A;
		int times = B.size() / A.size() + 2;
		for (int i = 1; i <= times; ++i) {
			if (str.find(B) != string::npos) {
				return i;
			}
			str+=A;
		}
		return -1;
	}
};

int main() {
	Solution solution;
	string a = "a";
	string b = "";
	cout << solution.repeatedStringMatch(a, b) << endl;
    return 0;
}